import { GraphQLSchema } from 'graphql';
import RootQueryType from './schema/query.js';
import RootMutationType from './schema/mutation.js';


// Define the schema
const schema = new GraphQLSchema({
  query: RootQueryType,
  mutation: RootMutationType
});



const app = express();

